mapper.delete:
delete from borrow
where UserID = #{userid,jdbcType=CHAR}
  and BookID = #{bookid,jdbcType=CHAR}

mapper.insert:
insert into borrow (UserID, BookID, BorrowTime, 
  ReturnTime, PenalSum, Payment
  )
values (#{userid,jdbcType=CHAR}, #{bookid,jdbcType=CHAR}, #{borrowtime,jdbcType=TIMESTAMP}, 
  #{returntime,jdbcType=TIMESTAMP}, #{penalsum,jdbcType=DECIMAL}, #{payment,jdbcType=DECIMAL}
  )

mapper.update:
update borrow
set BorrowTime = #{borrowtime,jdbcType=TIMESTAMP},
  ReturnTime = #{returntime,jdbcType=TIMESTAMP},
  PenalSum = #{penalsum,jdbcType=DECIMAL},
  Payment = #{payment,jdbcType=DECIMAL}
where UserID = #{userid,jdbcType=CHAR}
  and BookID = #{bookid,jdbcType=CHAR}

mapper.select:
select UserID, BookID, BorrowTime, ReturnTime, PenalSum, Payment
from borrow
where UserID = #{userid,jdbcType=CHAR}
  and BookID = #{bookid,jdbcType=CHAR}
