mapper.insert:
insert into depart (DepartID, DepartName, ParentDepartID
  )
values (#{departid,jdbcType=CHAR}, #{departname,jdbcType=VARCHAR}, #{parentdepartid,jdbcType=CHAR}
  )

mapper.update:
update depart
set DepartName = #{departname,jdbcType=VARCHAR},
  ParentDepartID = #{parentdepartid,jdbcType=CHAR}
where DepartID = #{departid,jdbcType=CHAR}

mapper.select:
select DepartID, DepartName, ParentDepartID
from depart
where DepartID = #{departid,jdbcType=CHAR}
